mapper.update:
update
pm_user_account set balance=#{balance}
where
user_id=#{userId}
and
bank_id=#{bankId}
and
acc_type_id=#{accTypId}

mapper.select:
select FIRST_NAME as firstName, LAST_NAME as lastName, USER_AGE as age, USER_ADDRESS as address, BANK_NAME as bankName, ACC_TYPE as accType, BALANCE as balance from pm_user , pm_bank , pm_acc_type , pm_user_account
pm_user_account.bank_id=pm_bank.bank_id and pm_user_account.user_id=pm_user.user_id and pm_user_account.acc_type_id=pm_acc_type.acc_type_id
AND pm_user.first_name=#{firstName}
AND pm_user.last_name=#{lastName}
AND pm_user.user_age=#{age}
